REPLACE INTO `demo` (`id`, `char`, `text`, `date`, `image`, `video`, `integer`, `decimal`, `boolean`, `select`, `multiselect`, `multicheckbox`, `link`, `html`) VALUES
	(167444, 'שלום רב שובך ציפורה נחמדת', 'if i\'ll say to you I love you\nwould you love me too?\nwould you take my hand in yours\nand run to Kathmandu?', '2013-03-13', 'img/2.jpg', 'video/1.flv', 556565, 2.76, 1, 3, '1,2,3', '1', 'http://www.google.com', '<div style="text-align: right;" dir="rtl">\n<h1 id="firstHeading" class="firstHeading" lang="he"><span dir="auto">ברצלונה (כדורגל)</span></h1>\n<div id="jump-to-nav" class="mw-jump">קפיצה אל: <a href="http://he.wikipedia.org/wiki/%D7%91%D7%A8%D7%A6%D7%9C%D7%95%D7%A0%D7%94_%28%D7%9B%D7%93%D7%95%D7%A8%D7%92%D7%9C%29#mw-head">ניווט</a>, <a href="http://he.wikipedia.org/wiki/%D7%91%D7%A8%D7%A6%D7%9C%D7%95%D7%A0%D7%94_%28%D7%9B%D7%93%D7%95%D7%A8%D7%92%D7%9C%29#p-search">חיפוש</a></div>\n<table class="infobox" style="width: 22em; font-size: 95%; border: 1px solid #aaaaaa; background-color: #f9f9f9; color: black; margin-bottom: 0.5em; margin-right: 1em;" cellspacing="5">\n<tbody>\n<tr><th style="text-align: center; font-size: 125%; font-weight: bold; background: #b0c4de;" colspan="2">ברצלונה</th></tr>\n<tr>\n<td style="text-align: center;" colspan="2"><a class="image" href="http://he.wikipedia.org/wiki/%D7%A7%D7%95%D7%91%D7%A5:Fcbarcelona.png"><img src="http://upload.wikimedia.org/wikipedia/he/thumb/9/9e/Fcbarcelona.png/170px-Fcbarcelona.png" alt="Fcbarcelona.png" width="170" height="173" /></a></td>\n</tr>\n<tr><th style="text-align: center; background: #b0c4de;" colspan="2">מידע כללי</th></tr>\n<tr><th style="text-align: right;" scope="row">כינוי</th>\n<td>בארסה, בלאוגרנה (כחול-ארגמן)</td>\n</tr>\n<tr><th style="text-align: right;" scope="row">תאריך ייסוד</th>\n<td><a title="1899" href="http://he.wikipedia.org/wiki/1899">1899</a></td>\n</tr>\n<tr><th style="text-align: right;" scope="row">אצטדיון</th>\n<td><a title="קאמפ נואו" href="http://he.wikipedia.org/wiki/%D7%A7%D7%90%D7%9E%D7%A4_%D7%A0%D7%95%D7%90%D7%95">קאמפ נואו</a>, <a title="ברצלונה" href="http://he.wikipedia.org/wiki/%D7%91%D7%A8%D7%A6%D7%9C%D7%95%D7%A0%D7%94